    private async Task ActualizarTurnos()
    {
        turnosEstados = new();
        turnosEstados = EstadosDiarios
                                       .OrderBy(o => o.Turno)
                                       .ThenBy(t => t.Orden)
                                       .GroupBy(g => g.Turno)
                                       .Select(s => new TurnoPuestos()
                                           {
                                               Turno = s.Key,
                                               PlanesDTO = s.ToList()
                                           })
                                        .ToList();
    }


